{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"name":"백준1002","private_outputs":true,"provenance":[],"collapsed_sections":[],"authorship_tag":"ABX9TyOywlnm3E8/C8n0qW1R5Onx"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","metadata":{"id":"5cF0w5600lVW"},"source":["# 터렛\n","T = int(input())\n","for i in range(T):\n","    x1, y1, r1, x2, y2, r2 = map(int, input().split())\n","    d = (((x1-x2)**2 + (y1-y2)**2)**(1/2))\n","    if x1 == x2 and y1 == y2 and r1 == r2:\n","        print(-1)\n","    elif d == r1 + r2 or d == abs(r1-r2):\n","        print(1)\n","    elif d > r1 + r2 or d < abs(r1-r2):\n","        print(0)\n","    else:\n","        print(2)"],"execution_count":null,"outputs":[]}]}