{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"name":"백준1316","private_outputs":true,"provenance":[],"collapsed_sections":[],"authorship_tag":"ABX9TyPzUrbYI9bM5KBiY2mhDpjY"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","metadata":{"id":"I5mr8SM8koD7"},"source":["# 그룹 단어 체커\n","N = int(input())\n","cnt = 0\n","for i in range(N):\n","    result = True\n","    word_list = []\n","    word = input()\n","    if len(word) == 1:\n","        cnt += 1\n","        continue\n","    for i in range(len(word)-1):\n","        if word[i] != word[i+1]:\n","            word_list.append(word[i])\n","    word_list.append(word[i+1])\n","    for j in word_list:\n","        if word_list.count(j) > 1:\n","            result = False\n","    if result == True:\n","        cnt += 1\n","print(cnt)"],"execution_count":null,"outputs":[]}]}