{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"name":"백준1620","private_outputs":true,"provenance":[],"collapsed_sections":[],"authorship_tag":"ABX9TyPv+hV1apQBNRr/buhTM4zr"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","metadata":{"id":"ShO6kWJCWPA5"},"source":["# 나는야 포켓몬 마스터 이다솜\n","import sys\n","N, M = map(int, input().split())\n","\n","pocket = {}\n","\n","for i in range(1, N+1):\n","    n = sys.stdin.readline().strip()\n","    pocket[n] = i\n","rev = {v:k for k,v in pocket.items()}\n","for i in range(M):\n","    m = sys.stdin.readline().strip()\n","    if m in pocket: # 문자를 입력받을 때\n","        print(pocket[m])\n","    else:   # 숫자를 입력받을 때\n","        print(rev[int(m)])"],"execution_count":null,"outputs":[]}]}