{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"name":"백준1991","private_outputs":true,"provenance":[],"collapsed_sections":[],"authorship_tag":"ABX9TyOVSP9UbRk/PaSozY14NC5e"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","metadata":{"id":"qftHDmm784op"},"source":["# 트리 순회\n","class Node:\n","    def __init__(self, root, left, right):\n","        self.root = root\n","        self.left = left\n","        self.right = right\n","\n","def preorder(input):\n","    print(input.root, end ='')\n","    if input.left != '.':\n","        preorder(tree[input.left])\n","    if input.right != '.':\n","        preorder(tree[input.right])\n","    \n","def inorder(input):\n","    if input.left != '.':\n","        inorder(tree[input.left])\n","    print(input.root, end ='')\n","    if input.right != '.':\n","        inorder(tree[input.right])\n","\n","def postorder(input):\n","    if input.left != '.':\n","        postorder(tree[input.left])\n","    if input.right != '.':\n","        postorder(tree[input.right])\n","    print(input.root, end ='')\n","\n","tree = {}\n","N = int(input())\n","for i in range(N):\n","    root, left, right = map(str, input().split())\n","    tree[root] = (Node(root, left, right))\n","\n","preorder(tree['A'])\n","print()\n","inorder(tree['A'])\n","print()\n","postorder(tree['A'])"],"execution_count":null,"outputs":[]}]}