{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"name":"백준20159","private_outputs":true,"provenance":[],"collapsed_sections":[],"authorship_tag":"ABX9TyO/r3V+SuvgKRWOcTviImk1"},"kernelspec":{"name":"python3","display_name":"Python 3"}},"cells":[{"cell_type":"code","metadata":{"id":"eU9pjOHFYVVK"},"source":["# 동작 그만. 밑장 빼기냐?\n","\n","N = int(input())\n","s = list(map(int,(input().split())))\n","me = []\n","you = []\n","for i in range(N):\n","    if i % 2 == 0:\n","        me.append(s[i])\n","    else:\n","        you.append(s[i])\n","\n","for i in range(len(me)):\n","    if you[-1] <= min(me):\n","        break\n","    if me[i] == min(me):\n","        me[i] = you[-1]\n","        you[i:] = me[i:] \n","        me[i+1:] = you[i:-1]\n","        break\n","\n","print(sum(me))"],"execution_count":null,"outputs":[]}]}