{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"name":"백준20546","private_outputs":true,"provenance":[],"collapsed_sections":[],"authorship_tag":"ABX9TyP/cI/Fs3lW/bttcpW6E7Fq"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","metadata":{"id":"3TaRiSBrEx9w"},"source":["# 🐜 기적의 매매법 🐜\n","money = int(input())\n","hyeon_out = money\n","hyeon_in = 0\n","min_out = money\n","min_in = 0\n","chart = list(map(int, input().split()))\n","\n","up_cnt = 0\n","down_cnt = 0\n","for i in range(len(chart)-1):\n","    if hyeon_out >= chart[i]:\n","        hyeon_in = chart[i]*(hyeon_out//chart[i])\n","        hyeon_out = hyeon_out - hyeon_in\n","    hyeon_in = (hyeon_in//chart[i]) * chart[i+1]  # 다음 날\n","\n","for i in range(len(chart)-2):\n","    if chart[i] < chart[i+1]:\n","        up_cnt += 1\n","    else:\n","        up_cnt = 0\n","    if chart[i] > chart[i+1]:\n","        down_cnt += 1\n","    else:\n","        down_cnt = 0\n","    if up_cnt >= 3:     # 3일연속 상승\n","        min_out = min_out + min_in\n","        min_in = 0\n","\n","    if down_cnt >= 3:   # 3일 연속 하락\n","        if min_out >= chart[i+1]:\n","            min_in = chart[i+1]*(min_out//chart[i+1])\n","            min_out = min_out - min_in\n","    min_in = (min_in//chart[i+1])*chart[i+2]   \n","\n","if hyeon_in+hyeon_out > min_in+min_out:\n","    print('BNP')\n","elif hyeon_in+hyeon_out < min_in+min_out:\n","    print('TIMING')\n","else:\n","    print('SAMESAME')"],"execution_count":null,"outputs":[]}]}