{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"name":"백준9375","private_outputs":true,"provenance":[],"collapsed_sections":[],"authorship_tag":"ABX9TyMPsDIVKeJ+g74t6OGPhURo"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","metadata":{"id":"9Z95jdWrMVmL"},"source":["# 패션왕 신해빈\n","import itertools\n","import sys\n","\n","T = int(input())\n","for _ in range(T):\n","    n = int(input())\n","    clothes = {}\n","\n","    for i in range(n):\n","        name, kind = input().split()\n","        if kind not in clothes:\n","            clothes[kind] = 1\n","        else:\n","            clothes[kind] += 1\n","\n","    sum = 0\n","    for i in clothes.values():\n","        sum += i\n","\n","    for i in range(2, len(clothes.values())+1):\n","        a = itertools.combinations(clothes.values(), i)\n","        for j in a:\n","            mult = 1\n","            for k in j:\n","                mult *= k\n","            sum += mult\n","    print(sum)"],"execution_count":null,"outputs":[]},{"cell_type":"code","metadata":{"id":"xKtXKwuL2vPy"},"source":[""],"execution_count":null,"outputs":[]}]}